import { Carousel } from "react-bootstrap";
import { useSelector } from "react-redux";
import "./buyerpage.scss";

let Buyerpage = ({ item },{CardUserId}) => {
  let productimage = item.productImage;
  let productsdetailed = useSelector((state) => state.productsdetailed);


let notification=()=>{
var userpost = Object.entries(productsdetailed).filter(item =>  item[0] === CardUserId).map(item => {return item[1]})



userpost.buy = "buynow"
console.log (userpost,"user add with buy property")
}

  return (
  <>
  <div className="carousel-first">
      <Carousel fade>
        {productimage.map((item, index) => {
          return (
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={productimage[index]}
                alt={index}
              />
            </Carousel.Item>
          );
        })}
      </Carousel>
    </div>

<div className="text">
{item.price}
</div >
<div className="text">
{item.category}
</div>
<div className="text">
{item.condition}
</div>
<div className="text">
{item.location}
</div>
<div className="text">
{item.discription}
</div>

<div>
  <button onClick={notification}>Buy now</button>
</div>
</>
  );
};
export default Buyerpage;
