import { useDispatch, useSelector } from "react-redux";
import "./card.scss";
import image from "../../assets/images/cross.jpg";
import {Toast} from 'react-bootstrap'
import firebase from "firebase";
import { useState } from "react";

let Card = ({ item, id, showCloseIcon,buyer }) => {
  let products = useSelector((state) => state.productsdetailed);
  let [toast,settoast] = useState(false)
  // console.log(buyer,"buy wala option")
  // let dispatch = useDispatch()

  if(buyer?.read){
    if(buyer?.buyrequest === "send"){
      // console.log(buyer.read,"true or false")
      // alert(buyer.Name + "want to buy your product")
      settoast(true)


      buyer.read = false
      // console.log(buyer,"items buy request")

      firebase
    .database()
    .ref("productsdetailed/" + id + "/buy/" )
    .set(buyer)
    .then(() => {
      // alert("seller read your message");
    })
    .catch(() => {
      alert("something went wrong");
    });
  }

    }




  // console.log(products,"all products") 
  let deletedata = () => {
    var allproducts = products;
    var afterdelete = Object.entries(allproducts).filter((data, index1) => {
      return id !== data[0];

    });
    afterdelete = new Map(afterdelete)
    afterdelete = Object.fromEntries(afterdelete)
    // console.log(afterdelete,"after deletes and converted")
    firebase
      .database()
      .ref("productsdetailed/")
      .set(afterdelete)
      .then(() => {
        alert("your AD deleted succesfully");
      })
      .catch(() => {
        alert("something went wrong");
      });
  };


  let toggleShowA =()=>{
    settoast(false)
  }
  return (
    <>
      <div className="card">
        {showCloseIcon ? (
          <div className="imagesss">
            <img src={image} onClick={()=>deletedata(id)} />
          </div>
        ) : (
          <></>
        )}
        <div className="imagess">
          <img src={item?.productImage[1]} />
        </div>
        <div className="price">
          <p>
            <b>{item.price}</b>
          </p>
        </div>
        <div className="discription">
          <span>{item.discription}</span>
        </div>
      </div>


        <Toast show={settoast} onClose={toggleShowA}>
          <Toast.Header>
            <img
              src="holder.js/20x20?text=%20"
              className="rounded mr-2"
              alt=""
            />
            <strong className="mr-auto">Bootstrap</strong>
            <small>11 mins ago</small>
          </Toast.Header>
          <Toast.Body>Woohoo, you're reading this text in a Toast!</Toast.Body>
        </Toast>


    </>
  );
};

export default Card;
