import { useDispatch, useSelector } from "react-redux";
import "./card.scss";
import image from "../../assets/images/cross.jpg";
import firebase from "firebase";

let Card = ({ item, index, showCloseIcon }) => {
  let products = useSelector((state) => state.productsdetailed);
  let dispatch = useDispatch()

  dispatch({
    type : "classUserId",
    payload: item.userID
  }) 

  // console.log(item.userID,"useriddddddd") 
  let deletedata = () => {
    var allproducts = products;
    var afterdelete = Object.values(allproducts).filter((data, index1) => {
      return item.userID !== data.userID;
    });

    firebase
      .database()
      .ref("productsdetailed/")
      .set(afterdelete)
      .then(() => {
        alert("your AD deleted succesfully");
      })
      .catch(() => {
        alert("something went wrong");
      });
  };

  return (
    <>
      <div className="card">
        {showCloseIcon ? (
          <div className="imagesss">
            <img src={image} onClick={deletedata} />
          </div>
        ) : (
          <></>
        )}
        <div className="imagess">
          <img src={item?.productImage[1]} />
        </div>
        <div className="price">
          <p>
            <b>{item.price}</b>
          </p>
        </div>
        <div className="discription">
          <span>{item.discription}</span>
        </div>
      </div>
    </>
  );
};

export default Card;
