import Signin from './components/authentication/signin/signin'
import Signup from './components/authentication/signup/signup'
import Olx from "./components/landing-page/olx"
import Productsdetailed from "./components/Add-products/products-detailed"
import Main from "./components/mainpage/mainpage"
import { BrowserRouter, Redirect, Route, Switch } from 'react-router-dom'
import { firebaseConfig } from "./firebase";
import { Spinner } from 'react-bootstrap';
import firebase from "firebase";
import { useDispatch, useSelector } from 'react-redux'
import { useEffect } from 'react'
import Userdeatailes from "./components/userdetailes/userdetailes"
firebase.initializeApp(firebaseConfig);

let App = () => {

  const dispatch = useDispatch()
  const userlength = useSelector(state => state.user)

useEffect(()=>{
  firebase.auth().onAuthStateChanged((user) => {
    if (user) {

      dispatch(
        {
          type: "user",
          payload: user
        }
        )
        firebase.database().ref(  'user/'+ userlength.uid  ).on( 'value', (snapshot) => {
          const data = snapshot.val() ? snapshot.val() : [];
          dispatch({
            type:"username",
            payload:data?.username
          })
        })  



        firebase.database().ref(  "productsdetailed/"  ).on( 'value', (snapshot) => {
          const data = snapshot.val() ? snapshot.val() : [];
          dispatch({
            type:"productsdetailed",
            payload:data
          })
        })


    }
    else {
      dispatch({
        type: "user",
        payload: false
      })
    }
  })  

},[userlength?.uid])








  if (userlength === "loading")
    return <div id="loader"><Spinner animation="border" role="status">
    </Spinner></div>


  return <>



    <BrowserRouter>
      <Switch>
        <PrivateRoute path={"/"}  exact SuccessComp={Main}    />
        <PrivateRoute path={"/signin"} exact  SuccessComp={Signin} failPath={"/"} />
        <PrivateRoute path={"/signup"} exact  SuccessComp={Signup} failPath={"/"} />
        <PrivateRoute path={"/products-detailed"} exact SuccessComp={Productsdetailed} failPath={"/signin"} />
        <PrivateRoute path={"/user-detailes"} exact SuccessComp={Userdeatailes} failPath={"/Olx"} />
      </Switch>

    </BrowserRouter>


  </>

}

let PrivateRoute = ({ condition, SuccessComp, failPath, ...rest }) => {
  return <Route {...rest} render={() => condition ? <SuccessComp /> : <Redirect to={failPath} />} />
}

export default App;
