import { Carousel } from "react-bootstrap";
import { useSelector } from "react-redux";
import "./buyerpage.scss";
import firebase from "firebase"

let Buyerpage = ({ item ,CardUserId}) => {
  let productimage = item.productImage;
  let productsdetailed = useSelector((state) => state.productsdetailed);


let notification=()=>{
var userpost = Object.entries(productsdetailed).filter(item =>  {return item[0] === CardUserId})
userpost.map(item => { 
  console.log(item[1],"by wali list")
  return item[1]})
// console.log(userpost[0],'asdaksjhdagfuia')
userpost[0][1].buy = {buyrequest:"send" , read:true,}
userpost = new Map(userpost)
userpost = Object.fromEntries(userpost)
// console.log (userpost,"user add with buy property")

firebase
      .database()
      .ref("productsdetailed/")
      .set(userpost)
      .then(() => {
        alert("your buying request is send succesfully");
      })
      .catch(() => {
        alert("something went wrong");
      });
  };

  return (
  <>
  <div className="carousel-first">
      <Carousel fade>
        {productimage.map((item, index) => {
          return (
            <Carousel.Item>
              <img
                className="d-block w-100"
                src={productimage[index]}
                alt={index}
              />
            </Carousel.Item>
          );
        })}
      </Carousel>
    </div>

<div className="text">
{item.price}
</div >
<div className="text">
{item.category}
</div>
<div className="text">
{item.condition}
</div>
<div className="text">
{item.location}
</div>
<div className="text">
{item.discription}
</div>

<div>
  <button onClick={notification}>Buy now</button>
</div>
</>
  );
};
export default Buyerpage;
